export function Button({ children, onClick, className = "" }) {
  return (
    <button
      className={`bg-black text-green-400 border border-green-400 font-mono py-2 px-4 rounded ${className}`}
      onClick={onClick}
      style={{
        boxShadow: "0 0 10px #33ff33",
        textShadow: "0 0 5px #33ff33",
      }}
    >
      {children}
    </button>
  );
}
