  // Initialize audio
  useEffect(() => {
    AudioManager.preloadSounds();
    // Play mysterious ambient sound for ancient terminal
    AudioManager.playAmbient('secure', 0.2);
    
    return () => {
      AudioManager.stopAmbient();
    };
