import React, { useState } from "react";
import { Card, CardContent } from "../ui/Card";
import { Input } from "../ui/Input";
import { Button } from "../ui/Button";
import { motion } from "framer-motion";

const terminalStyle = {
  fontFamily: "monospace",
  color: "#33ff33",
  backgroundColor: "black",
  padding: "20px",
  minHeight: "400px",
  border: "2px solid #33ff33",
  borderRadius: "5px",
  boxShadow: "0 0 10px #33ff33",
  whiteSpace: "pre-wrap"
};

const terminals = {
  "lysani01": { requiresRoll: 8, logs: "/logs/lysani01.json" },
  "s.elara01": { requiresRoll: false, logs: "/logs/s.elara01.json" },
  "slocombe875": { requiresRoll: 8, logs: "/logs/slocombe875.json" },
  "waferterm01": { requiresRoll: false, logs: "/logs/waferterm01.json" }
};

export default function TravellerTerminal() {
  const [inputCode, setInputCode] = useState("");
  const [terminalData, setTerminalData] = useState(null);
  const [logData, setLogData] = useState(null);
  const [rollCheck, setRollCheck] = useState(null);
  const [specialRollCheck, setSpecialRollCheck] = useState(null);
  const [activeTerminal, setActiveTerminal] = useState(null);
  const [selectedLog, setSelectedLog] = useState(null);

  const handleAccessCode = () => {
    const terminal = terminals[inputCode];
    if (terminal) {
        setActiveTerminal(terminal);
        if (terminal.requiresRoll) {
            setRollCheck({ difficulty: terminal.requiresRoll, success: null });
        } else {
            fetchLogs(terminal.logs);
        }
    } else {
        setTerminalData("ACCESS DENIED. INVALID CODE.");
    }
    setInputCode("");
  };

  const handleRollCheck = (passed) => {
    if (passed) {
        if (activeTerminal) {
            fetchLogs(activeTerminal.logs);
        } else {
            setTerminalData("ERROR: Terminal not found.");
        }
    } else {
        setTerminalData("ACCESS DENIED. INSUFFICIENT CLEARANCE.");
    }
    setRollCheck(null);
  };

  const handleSpecialRollCheck = (passed) => {
    if (passed) {
      setSelectedLog("Recovered from the computer of Astrogator Haris Solcombe's personal computer: Partial message recovered: '...if they find out... no one off this ship... contact Butler Chemical...'");
    } else {
      setSelectedLog("Decryption failed. Data permanently corrupted.");
    }
    setSpecialRollCheck(null);
  };

  const fetchLogs = async (logPath) => {
    try {
      const response = await fetch(logPath);
      const data = await response.json();
      setLogData(data);
    } catch (error) {
      setTerminalData("ERROR LOADING LOGS.");
    }
  };

  return (
    <div className="flex justify-center items-center h-screen bg-black">
      <Card className="w-[600px] border-green-400 border-2">
        <CardContent>
          <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 1 }}>
            <div style={terminalStyle} className="overflow-auto h-[300px]">
              {rollCheck ? (
                <div>
                  <p>Did you pass the {rollCheck.difficulty}+ Electronics (Computers) check?</p>
                  <Button onClick={() => handleRollCheck(true)}>Yes</Button>
                  <Button onClick={() => handleRollCheck(false)}>No</Button>
                </div>
              ) : specialRollCheck ? (
                <div>
                  <p>Did you pass the {specialRollCheck.difficulty}+ Electronics (Computers) check for "Erased Transmission"?</p>
                  <Button onClick={() => handleSpecialRollCheck(true)}>Yes</Button>
                  <Button onClick={() => handleSpecialRollCheck(false)}>No</Button>
                </div>
              ) : selectedLog ? (
                <div>
                  <p>{selectedLog}</p>
                  <Button onClick={() => setSelectedLog(null)}>Back</Button>
                </div>
              ) : logData ? (
                <div>
                  {logData.map((log, index) => (
                    <p
                      key={index}
                      onClick={() =>
                        log.title === "Erased Transmission"
                          ? setSpecialRollCheck({ difficulty: 10 })
                          : setSelectedLog(log.content)
                      }
                      style={{ cursor: "pointer", textDecoration: "underline" }}
                    >
                      {log.title}
                    </p>
                  ))}
                </div>
              ) : (
                <p>{terminalData || "ENTER ACCESS CODE TO PROCEED"}</p>
              )}
            </div>
          </motion.div>
          <div className="mt-4 flex gap-2">
            <Input
              className="text-green-400 bg-black border-green-400"
              placeholder="Enter Access Code..."
              value={inputCode}
              onChange={(e) => setInputCode(e.target.value)}
            />
            <Button onClick={handleAccessCode}>Enter</Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}
