export function Input({ value, onChange, placeholder }) {
  return (
    <input
      className="border border-green-400 bg-black text-green-400 p-2 w-full outline-none"
      type="text"
      value={value}
      onChange={onChange}
      placeholder={placeholder}
      style={{ fontFamily: "Courier New, monospace", textAlign: "center" }}
    />
  );
}
