export default function RiftjawTerminal({ onBack }) {
  const [currentLevel, setCurrentLevel] = useState(0); // 0-3 for levels 1-4
  const [step, setStep] = useState("content"); // "content", "password", "success", "final"
  const [displayedText, setDisplayedText] = useState("");
  const [typingComplete, setTypingComplete] = useState(false);
  const [passwordInput, setPasswordInput] = useState("");
  const [passwordAttempts, setPasswordAttempts] = useState(0);
  const typingRef = useRef(null);

  useEffect(() => {
    // Start with Level 1 content
    showLevelContent(0);
  }, []);

  const showLevelContent = (levelIndex) => {
    const level = authLevels[levelIndex];
    setDisplayedText("");
    setTypingComplete(false);
    setStep("content");
    
    const message = `${level.title}\n\nDate: Unknown\nAuthor: Ancient Security System\n\n${level.content}`;
    
    typeText(message, setDisplayedText, () => {
      setTypingComplete(true);
    });
  };

  const handleNext = () => {
    setStep("password");
    setPasswordInput("");
    setPasswordAttempts(0);
    setDisplayedText("");
    setTypingComplete(false);
    
    const level = authLevels[currentLevel];
    const message = `Password required for ${level.title}.\nAttempts remaining: 3\n\nEnter passphrase:`;
    
    typeText(message, setDisplayedText, () => {
      setTypingComplete(true);
    });
  };

  const handlePasswordSubmit = () => {
    const level = authLevels[currentLevel];
    
    if (passwordInput === level.password) {
      // Correct password
      setStep("success");
      setDisplayedText("");
      setTypingComplete(false);
      
      typeText(level.successMessage, setDisplayedText, () => {
        setTypingComplete(true);
        
        // Auto-advance after 2 seconds
        setTimeout(() => {
          if (currentLevel < authLevels.length - 1) {
            // Move to next level
            const nextLevel = currentLevel + 1;
            setCurrentLevel(nextLevel);
            showLevelContent(nextLevel);
          } else {
            // Show final archive
            setStep("final");
            setDisplayedText("");
            setTypingComplete(false);
            
            const finalMessage = `${finalArchive.title}\n\nDate: ${finalArchive.date}\nAuthor: ${finalArchive.author}\n\n${finalArchive.content}`;
            
            typeText(finalMessage, setDisplayedText, () => {
              setTypingComplete(true);
            });
          }
        }, 2000);
      });
    } else {
      // Wrong password
      const attempts = passwordAttempts + 1;
      setPasswordAttempts(attempts);
      setPasswordInput("");
      
      if (attempts >= 3) {
        // Max attempts reached - go back to main terminal
        setDisplayedText("");
        setTypingComplete(false);
        
        typeText("Maximum attempts exceeded. Access denied.", setDisplayedText, () => {
          setTypingComplete(true);
          setTimeout(() => {
            onBack();
          }, 2000);
        });
      } else {
        // Show failure message and let them try again
        setDisplayedText("");
        setTypingComplete(false);
        
        const failureMessage = `${level.failureMessage}\n\nAttempts remaining: ${3 - attempts}\n\nEnter passphrase:`;
        
        typeText(failureMessage, setDisplayedText, () => {
          setTypingComplete(true);
        });
      }
    }
  };

  return (
    <div className="flex flex-col items-center h-screen bg-black">
      <Card className="w-full max-w-md border-green-400 border-2">
        <CardContent>
          <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 1 }}>
            <div 
              className="terminal terminal-flicker"
              style={{ overflow: "auto", height: "300px", position: "relative" }}
            >
              <div style={{ whiteSpace: "pre-wrap", marginBottom: "10px" }}>
                {displayedText}
              </div>
              
              {/* Debug info */}
              <div style={{ fontSize: '10px', color: '#666', marginBottom: '10px' }}>
                DEBUG: Level {currentLevel + 1}, Step: {step}, Typing: {typingComplete ? 'Complete' : 'In Progress'}
              </div>
              
              {typingComplete && (
                <div>
                  {step === "content" && (
                    <Button
                      className="bg-green-400 text-black font-mono px-4 py-2 rounded hover:bg-green-500 mt-2"
                      onClick={handleNext}
                    >
                      Next
                    </Button>
                  )}
                  
                  {step === "password" && (
                    <div className="mt-2">
                      <Input
                        className="bg-black text-green-400 border border-green-400 px-3 py-2 font-mono focus:outline-none"
                        placeholder="Enter Passphrase"
                        value={passwordInput}
                        onChange={(e) => setPasswordInput(e.target.value)}
                        type="password"
                        onKeyPress={(e) => {
                          if (e.key === 'Enter') {
                            handlePasswordSubmit();
                          }
                        }}
                      />
                      <div className="flex gap-2 mt-2">
                        <Button
                          className="bg-green-400 text-black font-mono px-4 py-2 rounded hover:bg-green-500"
                          onClick={handlePasswordSubmit}
                        >
                          Submit
                        </Button>
                      </div>
                    </div>
                  )}
                  
                  {(step === "final" || step === "success") && (
                    <Button
                      className="bg-green-400 text-black font-mono px-4 py-2 rounded hover:bg-green-500 mt-2"
                      onClick={onBack}
                    >
                      Back
                    </Button>
                  )}
                </div>
              )}
              
              {/* Always show back button */}
              <Button
                className="bg-green-400 text-black font-mono px-4 py-2 rounded hover:bg-green-500 mt-2"
                onClick={onBack}
              >
                Back to Terminal
              </Button>
            </div>
          </motion.div>
        </CardContent>
      </Card>
    </div>
  );
}
